import React from 'react';
import { StyleSheet, Text, View, TouchableOpacity} from 'react-native';


export default function MainScreen(props){
    return(
        <View style={styles.container}>
            <Text style={[styles.title]}>Pay Your Expenses</Text>
            <TouchableOpacity style={[styles.orangeButton,{alignSelf:'flex-end'}]}>
                <Text style={{color:'white'}}>Pay</Text>
            </TouchableOpacity>
        </View>
    );
}


const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'flex-start',
      padding:20
    },
    orangeButton:{
        color:'red',
        backgroundColor: 'red',
        padding:12
    },
    title:{
        fontSize:20,
        fontWeight:'bold'
    }
  });
